class Bible {
  String title;
  List<Book> bookList;
  Book book;

  Bible({this.title, this.bookList, this.book});

  Map<String, dynamic> toMap() {
    var map = new Map<String, dynamic>();
    map["title"] = title;
    if (bookList != null) map['book'] = bookList;
    else map['book'] = book;
    return map;
  }

  Bible.fromMap(Map<String, dynamic> map) {
    title = map['title'];

    if (map['book'] != null) {
      bookList = new List<Book>();
      if (map['book'] is List) {
        map['book'].forEach((v) {
          bookList.add(new Book.fromMap(v));
        });
      } else {
        book = new Book.fromMap(map['book']);
      }
    }
  }




