class Book {
  String bookId;
  String title;
  Chapter chapter;
  String name;

  Book({this.bookId, this.title, this.chapter});

  Map<String, dynamic> toMap() {
    var map = new Map<String, dynamic>();
    map["book_id"] = bookId;
    map["title"] = title;
    map["chapter"] = chapter;
    map["name"] = name;

    return map;
  }


  Book.fromMap(Map<String, dynamic> map) {
    bookId = map['book_id'];
    title = map['title'];
    name = map['name'];
    chapter =
    map['chapter'] != null ? new Chapter.fromMap(map['chapter']) : null;
  }
